import osimport timefrom machine import Pin,PWMRGBR = Pin(27,Pin.OUT,1)RGBG = Pin(33,Pin.OUT,1)RGBB = Pin(32,Pin.OUT,1)def main():  pwmr = PWM(RGBR,1000,0)  pwmg = PWM(RGBG,1000,0)  pwmb = PWM(RGBB,1000,0)  while True:    val = 0    if val%6==0:      pwmr.duty(pwmr.duty()+10 if pwmr.duty()<900 else 0)    if val%4==0:      pwmg.duty(pwmg.duty()+10 if pwmg.duty()<900 else 0)    if val%2==0:      pwmb.duty(pwmb.duty()+10 if pwmb.duty()<900 else 0)    time.sleep_ms(50)    val=val+1